mapper.sql:
and ( select count(1) from tower_user_tx_source ts left join tower_order_detail td on ts.Order_id=td.id left join tower_tower_info ti on ti.id=td.Tower_id left join tower_area ta on ta.ID=ti.Tower_area left join Tower_district tdi on tdi.Dis_id=ti.Tower_district left join Tower_city tc on tc.City_id=ti.Tower_city and ts.Tx_id=r.id
and (ti.Tower_name like '%${parameter.towername}%' or ti.Tower_id '%${parameter.towerno}')
and ti.Tower_id = #{parameter.towerno}
)>0

mapper.select:
select * from (select *,ROW_NUMBER() OVER (ORDER BY adddate desc) as rank from( select r.ID as id, u.User_name as username, u.User_tele as usertele, c.City_name as cityname, d.Dis_name as districtname, l.levelname as levelname, t.Type_name as typename, r.Fee as fee, CONVERT(varchar, r.Add_date, 120 ) as adddate,
s.statename as statename, s.deal as isdeal, r.state, u.Wx_id as wxid, r.bank_back_id as bankbackid
) w1)as tm where tm.rank between #{iDisplayStart}+1 and #{iDisplayStart}+#{iDisplayLength} ;

mapper.update:
update tower_user_tx_record
set state = #{state}
where id = #{id};

update tower_user_charge
set Charge = Charge+#{fee},
Get_now=get_now+#{fee},
Getting_now=Getting_now-#{fee}
where Wx_id in (select Wx_id from tower_user_tx_record where Tx_id=
#{txid});

update tower_user_charge
set Getting_now = Getting_now-#{fee},
all_geting=all_geting+#{fee}
where Wx_id in (select Wx_id from tower_user_tx_record where Tx_id=
#{txid});

update tower_user_tx_record
set state=6
where id =#{id}
